import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { 
  Trash2, 
  Edit, 
  CheckCircle, 
  Upload, 
  Eye, 
  X, 
  Plus, 
  LayoutDashboard, 
  Calendar, 
  LogOut, 
  User,
  Search
} from 'lucide-react';

const TeacherDashboard = () => {
  const { logout, user } = useAuth();

  // Data State
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('ALL'); 

  // Modal States
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [currentId, setCurrentId] = useState(null);
  const [formData, setFormData] = useState({ title: '', description: '', dueDate: '' });

  // Submissions Modal State
  const [showSubModal, setShowSubModal] = useState(false);
  const [submissions, setSubmissions] = useState([]);
  const [selectedTitle, setSelectedTitle] = useState('');
  const [loadingSubs, setLoadingSubs] = useState(false);

  useEffect(() => {
    loadAssignments();
  }, []);

  const loadAssignments = async () => {
    setLoading(true);
    try {
      const data = await api.getAssignments();
      setAssignments(data);
    } catch (err) {
      console.error("Failed to load assignments", err);
    } finally {
      setLoading(false);
    }
  };

  // --- Helpers ---
  const formatDate = (dateString) => {
    if(!dateString) return '';
    return new Date(dateString).toLocaleDateString('en-US', {
        year: 'numeric', month: 'short', day: 'numeric'
    });
  };

  const getStatusStyle = (status) => {
    switch(status) {
        case 'Published': return 'bg-green-100 text-green-700 border-green-200';
        case 'Draft': return 'bg-yellow-50 text-yellow-700 border-yellow-200';
        case 'Completed': return 'bg-slate-100 text-slate-600 border-slate-200';
        default: return 'bg-gray-100 text-gray-600';
    }
  };

  // --- CRUD Actions ---
  const openCreateModal = () => {
    setFormData({ title: '', description: '', dueDate: '' });
    setIsEditing(false);
    setShowModal(true);
  };

  const openEditModal = (assign) => {
    const formattedDate = assign.dueDate ? new Date(assign.dueDate).toISOString().split('T')[0] : '';
    setFormData({ title: assign.title, description: assign.description, dueDate: formattedDate });
    setCurrentId(assign.id || assign._id);
    setIsEditing(true);
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        await api.updateAssignment(currentId, formData);
      } else {
        await api.createAssignment(formData);
      }
      setShowModal(false);
      loadAssignments();
    } catch (err) {
      alert("Error saving assignment: " + (err.response?.data?.message || err.message));
    }
  };

  const handleDelete = async (id) => {
    if(!window.confirm("Are you sure you want to delete this assignment?")) return;
    try {
      await api.deleteAssignment(id);
      loadAssignments();
    } catch (err) {
      alert("Error deleting: " + (err.response?.data?.message || err.message));
    }
  };

  const changeStatus = async (id, newStatus) => {
    try {
      await api.updateAssignmentStatus(id, newStatus);
      loadAssignments();
    } catch (err) {
      alert("Error updating status");
    }
  };

  // --- Submission View ---
  const handleViewSubmissions = async (assignmentId, title) => {
    setSelectedTitle(title);
    setShowSubModal(true);
    setLoadingSubs(true);
    setSubmissions([]); 
    try {
      const data = await api.getAssignmentSubmissions(assignmentId);
      setSubmissions(data);
    } catch (err) {
      alert("Failed to load submissions");
    } finally {
      setLoadingSubs(false);
    }
  };

  const filteredList = assignments.filter(a => filter === 'ALL' ? true : a.status === filter);

  return (
    <div className="min-h-screen bg-slate-50 font-sans">

      {/* --- Navbar --- */}
      <header className="bg-white shadow-sm border-b border-slate-200 sticky top-0 z-10 px-6 py-4">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
            <div className="flex items-center gap-2 text-indigo-700">
                <LayoutDashboard className="w-6 h-6" />
                <h1 className="text-xl font-bold tracking-tight">Teacher Portal</h1>
            </div>
            <div className="flex items-center gap-6">
                <div className="flex items-center gap-2 text-slate-600 bg-slate-100 px-3 py-1.5 rounded-full">
                    <User className="w-4 h-4" />
                    <span className="text-sm font-medium">{user?.name}</span>
                </div>
                <button onClick={logout} className="flex items-center gap-2 text-sm font-medium text-red-500 hover:text-red-600 transition">
                    <LogOut className="w-4 h-4" /> Logout
                </button>
            </div>
        </div>
      </header>

      {/* --- Main Content --- */}
      <main className="max-w-7xl mx-auto p-6">

        {/* Controls Bar */}
        <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
            <div className="bg-white p-1 rounded-lg border border-slate-200 shadow-sm flex gap-1">
                {['ALL', 'Draft', 'Published', 'Completed'].map(status => (
                    <button
                        key={status}
                        onClick={() => setFilter(status)}
                        className={`px-4 py-2 text-sm font-medium rounded-md transition-all ${
                            filter === status 
                            ? 'bg-indigo-50 text-indigo-700 shadow-sm' 
                            : 'text-slate-500 hover:bg-slate-50 hover:text-slate-700'
                        }`}
                    >
                        {status}
                    </button>
                ))}
            </div>

            <button 
                onClick={openCreateModal}
                className="bg-indigo-600 text-white px-5 py-2.5 rounded-lg shadow-sm hover:bg-indigo-700 transition flex items-center gap-2 font-medium"
            >
                <Plus className="w-5 h-5" /> New Assignment
            </button>
        </div>

        {/* Loading State */}
        {loading && (
            <div className="flex justify-center items-center py-20 text-slate-400">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
            </div>
        )}

        {/* Empty State */}
        {!loading && filteredList.length === 0 && (
            <div className="text-center py-20 bg-white rounded-xl border border-dashed border-slate-300">
                <div className="bg-slate-50 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                    <Search className="w-8 h-8 text-slate-400" />
                </div>
                <h3 className="text-lg font-medium text-slate-700">No assignments found</h3>
                <p className="text-slate-500 text-sm mt-1">Try changing the filter or create a new assignment.</p>
            </div>
        )}

        {/* Assignment Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {!loading && filteredList.map(assign => (
                <div key={assign._id || assign.id} className="group bg-white rounded-xl border border-slate-200 shadow-sm hover:shadow-md transition-all duration-200 flex flex-col">

                    {/* Card Header */}
                    <div className="p-5 flex-1">
                        <div className="flex justify-between items-start mb-3">
                            <span className={`px-2.5 py-0.5 rounded-full text-xs font-semibold border ${getStatusStyle(assign.status)}`}>
                                {assign.status}
                            </span>
                            <span className="flex items-center text-xs text-slate-500 bg-slate-50 px-2 py-1 rounded">
                                <Calendar className="w-3 h-3 mr-1" />
                                {formatDate(assign.dueDate)}
                            </span>
                        </div>
                        <h3 className="text-lg font-bold text-slate-800 mb-2 line-clamp-1" title={assign.title}>{assign.title}</h3>
                        <p className="text-slate-600 text-sm line-clamp-3 leading-relaxed">{assign.description}</p>
                    </div>

                    {/* Card Footer Actions */}
                    <div className="px-5 py-4 bg-slate-50 border-t border-slate-100 rounded-b-xl flex justify-between items-center">

                        {/* Left Side: Draft Actions */}
                        <div className="flex gap-2">
                            {assign.status === 'Draft' ? (
                                <>
                                    <button onClick={() => openEditModal(assign)} className="p-2 text-slate-500 hover:text-indigo-600 hover:bg-indigo-50 rounded-lg transition" title="Edit">
                                        <Edit className="w-4 h-4" />
                                    </button>
                                    <button onClick={() => handleDelete(assign._id || assign.id)} className="p-2 text-slate-500 hover:text-red-600 hover:bg-red-50 rounded-lg transition" title="Delete">
                                        <Trash2 className="w-4 h-4" />
                                    </button>
                                </>
                            ) : (
                                // View Submissions is primary action for Published/Completed
                                <button 
                                    onClick={() => handleViewSubmissions(assign._id || assign.id, assign.title)}
                                    className="flex items-center gap-1.5 text-sm font-medium text-indigo-600 hover:text-indigo-800 transition"
                                >
                                    <Eye className="w-4 h-4" /> Submissions
                                </button>
                            )}
                        </div>

                        {/* Right Side: State Transitions */}
                        <div>
                            {assign.status === 'Draft' && (
                                <button 
                                    onClick={() => changeStatus(assign._id || assign.id, 'Published')}
                                    className="flex items-center gap-1.5 text-xs font-semibold bg-green-100 text-green-700 px-3 py-1.5 rounded-md hover:bg-green-200 transition"
                                >
                                    <Upload className="w-3 h-3" /> Publish
                                </button>
                            )}
                            {assign.status === 'Published' && (
                                <button 
                                    onClick={() => changeStatus(assign._id || assign.id, 'Completed')}
                                    className="flex items-center gap-1.5 text-xs font-semibold bg-slate-800 text-white px-3 py-1.5 rounded-md hover:bg-slate-700 transition"
                                >
                                    <CheckCircle className="w-3 h-3" /> Close
                                </button>
                            )}
                             {/* If Draft, show View Submissions link in secondary spot */}
                             {assign.status === 'Draft' && (
                                <button onClick={() => handleViewSubmissions(assign._id || assign.id, assign.title)} className="sr-only">View</button> 
                             )}
                        </div>
                    </div>
                </div>
            ))}
        </div>
      </main>

      {/* --- CREATE/EDIT MODAL --- */}
      {showModal && (
        <div className="fixed inset-0 bg-slate-900/40 backdrop-blur-sm flex justify-center items-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-lg overflow-hidden animate-in fade-in zoom-in duration-200">
            <div className="px-6 py-4 border-b border-slate-100 flex justify-between items-center bg-slate-50">
                <h2 className="text-lg font-bold text-slate-800">{isEditing ? 'Edit Assignment' : 'Create New Assignment'}</h2>
                <button onClick={() => setShowModal(false)} className="text-slate-400 hover:text-slate-600"><X className="w-5 h-5" /></button>
            </div>

            <form onSubmit={handleSubmit} className="p-6 space-y-4">
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-1">Assignment Title</label>
                <input 
                  className="w-full border border-slate-300 p-2.5 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition" 
                  placeholder="e.g., Algebra Chapter 1"
                  required
                  value={formData.title}
                  onChange={e => setFormData({...formData, title: e.target.value})}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-1">Instructions / Description</label>
                <textarea 
                  className="w-full border border-slate-300 p-2.5 rounded-lg h-32 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none resize-none transition" 
                  placeholder="Details about the task..."
                  required
                  value={formData.description}
                  onChange={e => setFormData({...formData, description: e.target.value})}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-1">Due Date</label>
                <input 
                  type="date" 
                  className="w-full border border-slate-300 p-2.5 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition" 
                  required
                  value={formData.dueDate}
                  onChange={e => setFormData({...formData, dueDate: e.target.value})}
                />
              </div>
              <div className="pt-4 flex justify-end gap-3">
                <button type="button" onClick={() => setShowModal(false)} className="px-4 py-2 text-slate-600 hover:bg-slate-100 rounded-lg font-medium transition">Cancel</button>
                <button type="submit" className="bg-indigo-600 text-white px-6 py-2 rounded-lg hover:bg-indigo-700 font-medium transition shadow-sm">
                  {isEditing ? 'Save Changes' : 'Create Assignment'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* --- SUBMISSIONS MODAL --- */}
      {showSubModal && (
        <div className="fixed inset-0 bg-slate-900/40 backdrop-blur-sm flex justify-center items-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-4xl h-[85vh] flex flex-col animate-in fade-in zoom-in duration-200">

            {/* Header */}
            <div className="px-6 py-4 border-b border-slate-100 flex justify-between items-center bg-slate-50 rounded-t-xl">
                <div>
                    <h2 className="text-lg font-bold text-slate-800">Student Submissions</h2>
                    <p className="text-sm text-slate-500">For: <span className="font-medium text-indigo-600">{selectedTitle}</span></p>
                </div>
                <button onClick={() => setShowSubModal(false)} className="p-2 bg-white rounded-full border border-slate-200 text-slate-400 hover:text-red-500 hover:border-red-200 transition">
                    <X className="w-5 h-5" />
                </button>
            </div>

            {/* Body */}
            <div className="flex-1 overflow-y-auto p-6 bg-slate-50/50">
                {loadingSubs ? (
                     <div className="flex justify-center items-center h-40">
                        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
                     </div>
                ) : submissions.length === 0 ? (
                    <div className="flex flex-col items-center justify-center h-64 text-slate-400">
                        <div className="bg-slate-100 p-4 rounded-full mb-3">
                            <User className="w-8 h-8 text-slate-300" />
                        </div>
                        <p>No submissions received yet.</p>
                    </div>
                ) : (
                    <div className="grid grid-cols-1 gap-4">
                        {submissions.map((sub, index) => (
                            <div key={index} className="bg-white border border-slate-200 rounded-xl p-5 shadow-sm hover:shadow-md transition">
                                <div className="flex justify-between items-start mb-3 border-b border-slate-100 pb-3">
                                    <div className="flex items-center gap-3">
                                        <div className="w-10 h-10 bg-indigo-50 text-indigo-600 rounded-full flex items-center justify-center font-bold text-sm">
                                            {sub.studentId?.name?.charAt(0) || 'S'}
                                        </div>
                                        <div>
                                            <p className="font-semibold text-slate-800">{sub.studentId?.name || 'Unknown Student'}</p>
                                            <p className="text-xs text-slate-500">{sub.studentId?.email}</p>
                                        </div>
                                    </div>
                                    <span className="text-xs text-slate-500 bg-slate-50 px-2 py-1 rounded border border-slate-100">
                                        {new Date(sub.submittedDate).toLocaleString()}
                                    </span>
                                </div>
                                <div className="bg-slate-50 p-4 rounded-lg border border-slate-100 text-slate-700 whitespace-pre-wrap font-mono text-sm leading-relaxed">
                                    {sub.answer}
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {/* Footer */}
            <div className="px-6 py-4 border-t border-slate-100 bg-white rounded-b-xl flex justify-end">
                <button 
                    onClick={() => setShowSubModal(false)}
                    className="bg-slate-100 text-slate-700 px-5 py-2 rounded-lg hover:bg-slate-200 font-medium transition"
                >
                    Close View
                </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherDashboard;
