import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import { useAuth } from '../context/AuthContext';

const StudentDashboard = () => {
  const { logout, user } = useAuth();
  const [assignments, setAssignments] = useState([]);
  const [selectedAssign, setSelectedAssign] = useState(null);
  const [answer, setAnswer] = useState('');
  const [existingSubmission, setExistingSubmission] = useState(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    const data = await api.getPublishedAssignments();
    setAssignments(data);
  };

  const handleOpenAssignment = async (assign) => {
    setSelectedAssign(assign);
    setExistingSubmission(null);
    setAnswer('');

    // Check if already submitted
    const sub = await api.getMySubmission(assign.id, user.email);
    if(sub) {
      setExistingSubmission(sub);
      setAnswer(sub.answer);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.submitAssignment(selectedAssign.id, user.email, answer);
      alert('Assignment Submitted!');
      handleOpenAssignment(selectedAssign); // Reload state
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">Student Dashboard</h1>
        <div className="flex gap-4">
            <span className="self-center font-semibold">{user.name}</span>
            <button onClick={logout} className="text-red-500 hover:underline">Logout</button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Assignment List */}
        <div className="md:col-span-1 space-y-4">
          <h2 className="text-xl font-semibold text-gray-700">Assignments</h2>
          {assignments.length === 0 && <p className="text-gray-400">No active assignments.</p>}
          {assignments.map(a => (
            <div 
              key={a.id} 
              onClick={() => handleOpenAssignment(a)}
              className={`p-4 bg-white rounded shadow cursor-pointer border-l-4 hover:bg-blue-50 transition ${selectedAssign?.id === a.id ? 'border-blue-600 bg-blue-50' : 'border-gray-300'}`}
            >
              <h3 className="font-bold">{a.title}</h3>
              <p className="text-sm text-gray-500">Due: {a.dueDate}</p>
            </div>
          ))}
        </div>

        {/* Assignment Detail / Submission Area */}
        <div className="md:col-span-2">
          {selectedAssign ? (
            <div className="bg-white p-6 rounded shadow">
              <div className="mb-6 border-b pb-4">
                <h2 className="text-2xl font-bold">{selectedAssign.title}</h2>
                <p className="text-gray-700 mt-2">{selectedAssign.description}</p>
                <div className="mt-2 text-sm text-gray-500">Due Date: {selectedAssign.dueDate}</div>
              </div>

              {existingSubmission ? (
                <div className="bg-green-50 p-4 rounded border border-green-200">
                  <h3 className="text-green-800 font-bold mb-2">âœ“ Submitted on {new Date(existingSubmission.submittedDate).toLocaleDateString()}</h3>
                  <p className="text-gray-800 font-medium">Your Answer:</p>
                  <p className="text-gray-600 mt-1 whitespace-pre-wrap">{existingSubmission.answer}</p>
                </div>
              ) : (
                <form onSubmit={handleSubmit}>
                  <label className="block font-medium mb-2">Write your answer:</label>
                  <textarea 
                    className="w-full border p-3 rounded h-40 focus:ring-2 focus:ring-blue-500 outline-none"
                    placeholder="Type your answer here..."
                    required
                    value={answer}
                    onChange={e => setAnswer(e.target.value)}
                  ></textarea>
                  <div className="mt-4 flex justify-end">
                    <button type="submit" className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
                      Submit Assignment
                    </button>
                  </div>
                </form>
              )}
            </div>
          ) : (
            <div className="h-full flex items-center justify-center text-gray-400 border-2 border-dashed rounded">
              Select an assignment to view details
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
