import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { 
  BookOpen, 
  CheckCircle, 
  Clock, 
  Send, 
  FileText, 
  LogOut, 
  User,
  Calendar
} from 'lucide-react';

const StudentDashboard = () => {
  const { logout, user } = useAuth();

  // State
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedAssign, setSelectedAssign] = useState(null);

  // Submission State
  const [answer, setAnswer] = useState('');
  const [existingSubmission, setExistingSubmission] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const data = await api.getPublishedAssignments();
      setAssignments(data);
    } catch (err) {
      console.error("Failed to load assignments");
    } finally {
      setLoading(false);
    }
  };

  const handleOpenAssignment = async (assign) => {
    setSelectedAssign(assign);
    setExistingSubmission(null);
    setAnswer('');

    try {
      // Check if already submitted
      const sub = await api.getMySubmission(assign._id, user.email);
      if (sub) {
        setExistingSubmission(sub);
        setAnswer(sub.answer);
      }
    } catch (err) {
      console.error("Error fetching submission status");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {      
      await api.submitAssignment(selectedAssign._id, user.email, answer);

      // Refresh logic without full page reload
      const sub = await api.getMySubmission(selectedAssign._id, user.email);
      setExistingSubmission(sub);
      setAnswer(sub.answer);
      alert('Assignment Submitted Successfully!');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Helper to format date
  const formatDate = (dateString) => {
    if (!dateString) return 'No Date';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="min-h-screen bg-slate-50 flex flex-col">
      {/* --- Navbar --- */}
      <header className="bg-white shadow-sm border-b border-slate-200 px-6 py-4 flex justify-between items-center sticky top-0 z-10">
        <div className="flex items-center gap-2 text-blue-700">
          <BookOpen className="w-6 h-6" />
          <h1 className="text-xl font-bold tracking-tight">Student Portal</h1>
        </div>

        <div className="flex items-center gap-6">
          <div className="flex items-center gap-2 text-slate-600 bg-slate-100 px-3 py-1.5 rounded-full">
            <User className="w-4 h-4" />
            <span className="text-sm font-medium">{user?.name}</span>
          </div>
          <button 
            onClick={logout} 
            className="flex items-center gap-2 text-sm font-medium text-red-500 hover:text-red-600 transition"
          >
            <LogOut className="w-4 h-4" />
            Logout
          </button>
        </div>
      </header>

      {/* --- Main Content --- */}
      <main className="flex-1 max-w-7xl w-full mx-auto p-6">
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-6 h-[calc(100vh-8rem)]">

          {/* --- LEFT COLUMN: Assignment List --- */}
          <div className="lg:col-span-4 flex flex-col bg-white rounded-xl shadow-sm border border-slate-200 overflow-hidden">
            <div className="p-4 border-b border-slate-100 bg-slate-50">
              <h2 className="font-semibold text-slate-800">Available Assignments</h2>
              <p className="text-xs text-slate-500 mt-1">Select a task to view details</p>
            </div>

            <div className="flex-1 overflow-y-auto p-2 space-y-2 custom-scrollbar">
              {loading ? (
                <div className="text-center py-10 text-slate-400">Loading tasks...</div>
              ) : assignments.length === 0 ? (
                <div className="text-center py-10 text-slate-400">No assignments assigned yet.</div>
              ) : (
                assignments.map(a => (
                  <div 
                    key={a._id} 
                    onClick={() => handleOpenAssignment(a)}
                    className={`
                      group p-4 rounded-lg cursor-pointer transition-all duration-200 border
                      ${selectedAssign?._id === a._id 
                        ? 'bg-blue-50 border-blue-200 shadow-sm' 
                        : 'bg-white border-transparent hover:bg-slate-50 hover:border-slate-200'}
                    `}
                  >
                    <div className="flex justify-between items-start mb-1">
                      <h3 className={`font-semibold text-sm ${selectedAssign?._id === a._id ? 'text-blue-700' : 'text-slate-700'}`}>
                        {a.title}
                      </h3>
                      {/* We could add logic here to check 'completed' status from list if available */}
                    </div>
                    <div className="flex items-center gap-2 text-xs text-slate-500">
                      <Calendar className="w-3 h-3" />
                      <span>Due: {formatDate(a.dueDate)}</span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* --- RIGHT COLUMN: Details & Submission --- */}
          <div className="lg:col-span-8 flex flex-col bg-white rounded-xl shadow-sm border border-slate-200 overflow-hidden">
            {selectedAssign ? (
              <>
                {/* Header */}
                <div className="p-6 border-b border-slate-100">
                  <div className="flex justify-between items-start">
                    <div>
                      <h2 className="text-2xl font-bold text-slate-800">{selectedAssign.title}</h2>
                      <div className="flex items-center gap-2 mt-2 text-sm text-slate-500">
                        <Clock className="w-4 h-4" />
                        <span>Due Date: <span className="font-medium text-slate-700">{formatDate(selectedAssign.dueDate)}</span></span>
                      </div>
                    </div>
                    <div className={`px-3 py-1 rounded-full text-xs font-semibold border ${existingSubmission ? 'bg-green-50 text-green-700 border-green-200' : 'bg-yellow-50 text-yellow-700 border-yellow-200'}`}>
                      {existingSubmission ? 'Submitted' : 'Pending'}
                    </div>
                  </div>
                </div>

                {/* Content Scroll Area */}
                <div className="flex-1 overflow-y-auto p-6">
                  {/* Description */}
                  <div className="mb-8">
                    <h3 className="text-sm font-semibold text-slate-900 uppercase tracking-wider mb-2">Description</h3>
                    <div className="bg-slate-50 p-4 rounded-lg text-slate-700 leading-relaxed border border-slate-100">
                      {selectedAssign.description}
                    </div>
                  </div>

                  {/* Submission Area */}
                  <div>
                    <h3 className="text-sm font-semibold text-slate-900 uppercase tracking-wider mb-2">Your Submission</h3>

                    {existingSubmission ? (
                      <div className="bg-green-50 border border-green-200 rounded-lg p-5">
                        <div className="flex items-center gap-2 text-green-800 font-semibold mb-3">
                          <CheckCircle className="w-5 h-5" />
                          <span>Submitted on {formatDate(existingSubmission.submittedDate)}</span>
                        </div>
                        <div className="bg-white p-4 rounded border border-green-100 text-slate-700 whitespace-pre-wrap shadow-sm">
                          {existingSubmission.answer}
                        </div>
                      </div>
                    ) : (
                      <form onSubmit={handleSubmit} className="relative">
                        <textarea 
                          className="w-full border border-slate-300 rounded-lg p-4 h-48 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none resize-none text-slate-700 transition"
                          placeholder="Type your answer or solution here..."
                          required
                          value={answer}
                          onChange={e => setAnswer(e.target.value)}
                        ></textarea>

                        <div className="mt-4 flex justify-end">
                          <button 
                            type="submit" 
                            disabled={isSubmitting || !answer.trim()}
                            className="bg-blue-600 text-white px-6 py-2.5 rounded-lg hover:bg-blue-700 disabled:bg-blue-300 disabled:cursor-not-allowed transition flex items-center gap-2 font-medium shadow-sm"
                          >
                            {isSubmitting ? (
                              <span>Sending...</span>
                            ) : (
                              <>
                                <Send className="w-4 h-4" /> Submit Assignment
                              </>
                            )}
                          </button>
                        </div>
                      </form>
                    )}
                  </div>
                </div>
              </>
            ) : (
              /* Empty State */
              <div className="h-full flex flex-col items-center justify-center text-slate-400 bg-slate-50/50">
                <div className="bg-white p-4 rounded-full shadow-sm mb-4">
                  <FileText className="w-10 h-10 text-slate-300" />
                </div>
                <p className="text-lg font-medium text-slate-500">No Assignment Selected</p>
                <p className="text-sm">Click on an assignment from the list to view details.</p>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default StudentDashboard;
