import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { Trash2, Edit, CheckCircle, Upload } from 'lucide-react'; // Install: npm install lucide-react

const TeacherDashboard = () => {
  const { logout, user } = useAuth();
  const [assignments, setAssignments] = useState([]);
  const [filter, setFilter] = useState('ALL');

  // Modal State
  const [showModal, setShowModal] = useState(false);
  const [currentId, setCurrentId] = useState(null);
  const [formData, setFormData] = useState({ title: '', description: '', dueDate: '' });

  useEffect(() => {
    loadAssignments();
  }, []);

  const loadAssignments = async () => {
    try {
      const data = await api.getAssignments();
      setAssignments(data);
    } catch (err) {
      console.error("Failed to load assignments", err);
    }
  };

  // Open Modal for Create
  const openCreateModal = () => {
    setFormData({ title: '', description: '', dueDate: '' });
    setShowModal(true);
  };

  // Open Modal for Edit
  const openEditModal = (assign) => {
    // Format date for input (YYYY-MM-DD)
    const formattedDate = assign.dueDate ? new Date(assign.dueDate).toISOString().split('T')[0] : '';
    setFormData({ title: assign.title, description: assign.description, dueDate: formattedDate });
    setCurrentId(assign.id || assign._id);
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.createAssignment(formData);
      setShowModal(false);
      loadAssignments();
    } catch (err) {
      alert("Error saving assignment: " + (err.response?.data?.message || err.message));
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this assignment?")) return;
    try {
      await api.deleteAssignment(id);
      loadAssignments();
    } catch (err) {
      alert("Error deleting: " + (err.response?.data?.message || err.message));
    }
  };

  const changeStatus = async (id, newStatus) => {
    try {
      await api.updateAssignmentStatus(id, newStatus);
      loadAssignments();
    } catch (err) {
      alert("Error updating status");
    }
  };

  const handleViewSubmissions = async (assignmentId, title) => {
    try {
      setSubmissions([]); // Clear previous data
      setSelectedTitle(title);
      setShowSubModal(true);
      const data = await api.getAssignmentSubmissions(assignmentId);
      setSubmissions(data);
    } catch (err) {
      alert("Failed to load submissions: " + (err.response?.data?.message || err.message));
    }
  };

  // Filter Logic
  const filteredList = assignments.filter(a => filter === 'ALL' ? true : a.status === filter);

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      {/* Header */}
      <div className="flex justify-between items-center mb-8 bg-white p-4 rounded shadow-sm">
        <h1 className="text-2xl font-bold text-gray-800">Teacher Dashboard</h1>
        <div className="flex items-center gap-4">
          <span className="font-semibold text-gray-700">Hello, {user?.name}</span>
          <button onClick={logout} className="text-red-500 hover:bg-red-50 px-3 py-1 rounded border border-red-200">Logout</button>
        </div>
      </div>

      {/* Controls */}
      <div className="flex justify-between mb-6">
        <div className="space-x-2">
          {['ALL', 'Draft', 'Published', 'Completed'].map(status => (
            <button
              key={status}
              onClick={() => setFilter(status)}
              className={`px-4 py-2 text-sm font-medium rounded transition ${filter === status ? 'bg-blue-600 text-white shadow' : 'bg-white text-gray-600 border hover:bg-gray-50'}`}
            >
              {status}
            </button>
          ))}
        </div>
        <button
          onClick={openCreateModal}
          className="bg-green-600 text-white px-4 py-2 rounded shadow hover:bg-green-700 flex items-center gap-2"
        >
          <span>+ New Assignment</span>
        </button>
      </div>

      {/* List */}
      <div className="grid gap-4">
        {filteredList.map(assign => (
          <div key={assign._id || assign.id} className="bg-white p-6 rounded shadow-sm border border-gray-200 hover:shadow-md transition">
            <div className="flex justify-between items-start">
              <div>
                <div className="flex items-center gap-2 mb-1">
                  <h3 className="text-xl font-bold text-gray-800">{assign.title}</h3>
                  <span className={`text-xs px-2 py-1 rounded-full font-semibold ${assign.status === 'Draft' ? 'bg-yellow-100 text-yellow-800' :
                    assign.status === 'Published' ? 'bg-green-100 text-green-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                    {assign.status}
                  </span>
                </div>
                <p className="text-gray-600 mb-2">{assign.description}</p>
                <div className="text-sm text-gray-500">
                  Due: {new Date(assign.dueDate).toLocaleDateString()}
                </div>
              </div>

              <div className="flex flex-col gap-2 items-end">
                {/* Action Buttons Based on Status */}
                {assign.status === 'Draft' && (
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleDelete(assign._id || assign.id)}
                      className="text-gray-500 hover:text-red-600 p-2 rounded hover:bg-red-50"
                      title="Delete"
                    >
                      <Trash2 size={18} />
                    </button>
                    <button
                      onClick={() => changeStatus(assign._id || assign.id, 'Published')}
                      className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700 flex items-center gap-1"
                    >
                      <Upload size={14} /> Publish
                    </button>
                  </div>
                )}

                {assign.status === 'Published' && (
                  <button
                    onClick={() => changeStatus(assign._id || assign.id, 'Completed')}
                    className="bg-gray-700 text-white px-3 py-1 rounded text-sm hover:bg-gray-800 flex items-center gap-1"
                  >
                    <CheckCircle size={14} /> Mark Completed
                  </button>
                )}

                <button className="text-blue-600 text-sm hover:underline mt-2">View Submissions</button>
              </div>
            </div>
          </div>
        ))}
        {filteredList.length === 0 && <p className="text-center text-gray-400 py-10">No assignments found.</p>}
      </div>

      {/* Create Model */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded shadow-lg w-full max-w-md mx-4">
            <h2 className="text-xl font-bold mb-4">{'Create Assignment'}</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label className="block text-sm font-medium text-gray-700 mb-1">Title</label>
                <input
                  className="w-full border p-2 rounded focus:ring-2 focus:ring-blue-500 outline-none"
                  required
                  value={formData.title}
                  onChange={e => setFormData({ ...formData, title: e.target.value })}
                />
              </div>
              <div className="mb-3">
                <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                <textarea
                  className="w-full border p-2 rounded h-24 focus:ring-2 focus:ring-blue-500 outline-none"
                  required
                  value={formData.description}
                  onChange={e => setFormData({ ...formData, description: e.target.value })}
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">Due Date</label>
                <input
                  type="date"
                  className="w-full border p-2 rounded focus:ring-2 focus:ring-blue-500 outline-none"
                  required
                  value={formData.dueDate}
                  onChange={e => setFormData({ ...formData, dueDate: e.target.value })}
                />
              </div>
              <div className="flex justify-end gap-2">
                <button type="button" onClick={() => setShowModal(false)} className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded">Cancel</button>
                <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
                  {'Create'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherDashboard;
