import axios from 'axios';

// Base API URL (Ensure your backend is running on this port)
const API_URL = 'http://localhost:5000/api';

// Create Axios Instance
const apiClient = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Interceptor: Attach JWT Token to every request if it exists
apiClient.interceptors.request.use(
  (config) => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const { token } = JSON.parse(storedUser);
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export const api = {
  // Auth
  login: async (email, password) => {
    const response = await apiClient.post('/auth/login', { email, password });
    return response.data; // Returns { token, role, name, email }
  },

  register: async (name, email, password, role) => {
    const response = await apiClient.post('/auth/register', { name, email, password, role });
    return response.data;
  },

  // Teacher: Get My Assignments
  getAssignments: async () => {
    const response = await apiClient.get('/assignments/teacher');
    return response.data;
  },

  // Student: Get Published Assignments Only
  getPublishedAssignments: async () => {
    const response = await apiClient.get('/assignments/published');
    return response.data;
  },

  createAssignment: async (data) => {
    const response = await apiClient.post('/assignments', data);
    return response.data;
  },

  updateAssignmentStatus: async (id, status) => {
    // Backend expects { status: "Published" } etc.
    const response = await apiClient.put(`/assignments/${id}/status`, { status });
    return response.data;
  },

  // Student: Submit Answer
  submitAssignment: async (assignmentId, studentEmail, answer) => {
    // Note: Backend extracts student ID from Token, so we just send assignmentId and answer
    const response = await apiClient.post('/submissions', { assignmentId, answer });
    return response.data;
  },

  // Student: Check if I submitted
  getMySubmission: async (assignmentId, studentEmail) => {
    const response = await apiClient.get(`/submissions/me/${assignmentId}`);
    return response.data;
  },

  // Teacher: Get all submissions for an assignment
  getAssignmentSubmissions: async (assignmentId) => {
    const response = await apiClient.get(`/submissions/assignment/${assignmentId}`);
    return response.data;
  }
};
