import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import { useAuth } from '../context/AuthContext';

const TeacherDashboard = () => {
  const { logout, user } = useAuth();
  const [assignments, setAssignments] = useState([]);
  const [filter, setFilter] = useState('ALL'); // ALL, DRAFT, PUBLISHED, COMPLETED
  const [showModal, setShowModal] = useState(false);
  const [newAssign, setNewAssign] = useState({ title: '', description: '', dueDate: '' });

  // Load Data
  useEffect(() => {
    loadAssignments();
  }, []);

  const loadAssignments = async () => {
    const data = await api.getAssignments();
    setAssignments(data);
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    await api.createAssignment(newAssign);
    setShowModal(false);
    setNewAssign({ title: '', description: '', dueDate: '' });
    loadAssignments();
  };

  const changeStatus = async (id, newStatus) => {
    await api.updateAssignmentStatus(id, newStatus);
    loadAssignments();
  };

  // Filter Logic
  const filteredList = assignments.filter(a => filter === 'ALL' ? true : a.status === filter);

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">Teacher Dashboard</h1>
        <div className="flex gap-4">
            <span className="self-center font-semibold">{user.name}</span>
            <button onClick={logout} className="text-red-500 hover:underline">Logout</button>
        </div>
      </div>

      {/* Controls */}
      <div className="flex justify-between mb-6">
        <div className="space-x-2">
          {['ALL', 'DRAFT', 'PUBLISHED', 'COMPLETED'].map(status => (
            <button
              key={status}
              onClick={() => setFilter(status)}
              className={`px-4 py-2 rounded ${filter === status ? 'bg-blue-600 text-white' : 'bg-white text-gray-600 border'}`}
            >
              {status}
            </button>
          ))}
        </div>
        <button 
          onClick={() => setShowModal(true)}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          + New Assignment
        </button>
      </div>

      {/* List */}
      <div className="grid gap-4">
        {filteredList.map(assign => (
          <div key={assign.id} className="bg-white p-6 rounded shadow border-l-4 border-blue-500">
            <div className="flex justify-between">
              <div>
                <h3 className="text-xl font-bold">{assign.title}</h3>
                <p className="text-gray-600 mt-1">{assign.description}</p>
                <div className="mt-2 text-sm text-gray-500">
                  Due: {assign.dueDate} | Status: <span className="font-semibold">{assign.status}</span>
                </div>
              </div>

              <div className="flex flex-col gap-2">
                {assign.status === 'DRAFT' && (
                  <button 
                    onClick={() => changeStatus(assign.id, 'PUBLISHED')}
                    className="bg-indigo-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Publish
                  </button>
                )}
                {assign.status === 'PUBLISHED' && (
                  <button 
                    onClick={() => changeStatus(assign.id, 'COMPLETED')}
                    className="bg-gray-700 text-white px-3 py-1 rounded text-sm"
                  >
                    Close (Complete)
                  </button>
                )}
                <button className="text-blue-600 text-sm hover:underline">View Submissions</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Create Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded w-96">
            <h2 className="text-xl font-bold mb-4">Create Assignment</h2>
            <form onSubmit={handleCreate}>
              <input 
                className="w-full border p-2 mb-2 rounded" 
                placeholder="Title" 
                required
                value={newAssign.title}
                onChange={e => setNewAssign({...newAssign, title: e.target.value})}
              />
              <textarea 
                className="w-full border p-2 mb-2 rounded" 
                placeholder="Description" 
                required
                value={newAssign.description}
                onChange={e => setNewAssign({...newAssign, description: e.target.value})}
              />
              <input 
                type="date" 
                className="w-full border p-2 mb-4 rounded" 
                required
                value={newAssign.dueDate}
                onChange={e => setNewAssign({...newAssign, dueDate: e.target.value})}
              />
              <div className="flex justify-end gap-2">
                <button type="button" onClick={() => setShowModal(false)} className="text-gray-500">Cancel</button>
                <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Create</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherDashboard;
